    private void View_TotalActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_View_TotalActionPerformed
        String total = Stored_Total.totalSales.toString().replaceAll("\\[", "  \\$").replaceAll("\\]", "\\\n").replaceAll("\\, ", "\\\n  \\$");        // replaces character in Array List
        Sales.setText( total +"+__________\n$" + Stored_Total.sumTotal() + "   <--- Grand Total");    
    }//GEN-LAST:event_View_TotalActionPerformed

