    private void TransactionsItemStateChanged(java.awt.event.ItemEvent evt) {//GEN-FIRST:event_TransactionsItemStateChanged
        int i = Transactions.getSelectedIndex();
        Sales.setText( Transactions.getItemAt(i) + "\n" + Stored_Sales.getSpecificValue(i-1));    //adds Titale to text field
    }//GEN-LAST:event_TransactionsItemStateChanged

